bool get_hold_on_other_key_press(uint16_t keycode, keyrecord_t *record) {
    switch (keycode) {
        case LSFT_T(KC_UNDS):
            return true;
        case LT(4, KC_TAB):
            return true;
        case LT(2, KC_TAB):
            return true;
        case LGUI_T(KC_SPC):
            return true;
        case LSFT_T(KC_UP):
            return true;
        case RCTL_T(KC_ENT):
            return true;
        default:
            // Do not select the hold action when another key is pressed.
            return false;
    }
}

uint8_t mod_state;
bool    process_record_user(uint16_t keycode, keyrecord_t *record) {
#ifdef CONSOLE_ENABLE
    uprintf("KL: kc: 0x%04X, col: %2u, row: %2u, pressed: %u, time: %5u, int: %u, count: %u\n", keycode, record->event.key.col, record->event.key.row, record->event.pressed, record->event.time, record->tap.interrupted, record->tap.count);
    uprintf("mod %2u: ", get_mods());
    uprintf("shift mod mask: %2u", MOD_MASK_SHIFT);
    uprintf("mod == shift: %u\n", get_mods() & MOD_MASK_SHIFT);
#endif
    switch (keycode) {
        case LCTL_T(KC_NO):
            if (record->tap.count && record->event.pressed) {
                tap_code16(C(KC_SPC));
                return false;
            }
            break;
        case LSFT_T(KC_UNDS):
            if (record->tap.count && record->event.pressed) {
                tap_code16(KC_UNDS);
                return false;
            }
            break;
        case LT(3, KC_LPRN):
            if (record->tap.count && record->event.pressed) {
                tap_code16(KC_LPRN);
                return false;
            }
            break;
        case LCTL_T(KC_LCBR):
            if (record->tap.count && record->event.pressed) {
                mod_state = get_mods();
                if (mod_state & MOD_MASK_SHIFT) {
                    del_mods(MOD_MASK_SHIFT);
                    tap_code16(KC_LBRC);
                    set_mods(mod_state);
                } else {
                    tap_code16(KC_LCBR);
                }
                return false;
            }
            break;
        case LT(2, KC_RCBR):
            if (record->tap.count && record->event.pressed) {
                mod_state = get_mods();
                if (mod_state & MOD_MASK_SHIFT) {
                    del_mods(MOD_MASK_SHIFT);
                    tap_code16(KC_RBRC);
                    set_mods(mod_state);
                } else {
                    tap_code16(KC_RCBR);
                }
                return false;
            }
            break;

        case LCTL_T(KC_COLN):
            if (record->tap.count && record->event.pressed) {
                tap_code16(KC_COLN);
                return false;
            }
            break;
        case LT(2, KC_DQUO):
            if (record->tap.count && record->event.pressed) {
                tap_code16(KC_DQUO);
                return false;
            }
            break;
        case RALT_T(KC_RPRN):
            if (record->tap.count && record->event.pressed) {
                mod_state = get_mods();
                if (mod_state & MOD_MASK_SHIFT) {
                    tap_code16(KC_QUOT);
                } else {
                    tap_code16(KC_RPRN);
                }
                return false;
            }
            break;
        case LT(3, KC_UNDS):
            if (record->tap.count && record->event.pressed) {
                tap_code16(KC_UNDS);
                return false;
            }
            break;
    }
    return true;
}

