uint16_t get_tapping_term(uint16_t keycode, keyrecord_t *record) {
    switch (keycode) {
        case LCTL_T(KC_LCBR):
            return TAPPING_TERM + 500;
        case LT(2, KC_RCBR):
            return TAPPING_TERM + 500;
        case LT(3, KC_LPRN):
            return TAPPING_TERM + 500;
        case RALT_T(KC_RPRN):
            return TAPPING_TERM + 500;
        default:
            return TAPPING_TERM;
    }
}
